import { Injectable, OnInit } from '@angular/core';
import { BookModel } from '../model/book.model';
import { HttpClient } from '@angular/common/http';

@Injectable()
export class BookService {
  public books: BookModel[] = [];

  constructor(private http: HttpClient) {
    const response = this.http.get('https://my.api.mockaroo.com/api/v1/books?key=2103c2f0');
    response.subscribe((item: BookModel[]) => this.books = item);
  }

  add(): void {
    const lastId = this.books[this.books.length - 1].uid;
    const newId = lastId + 1;

    const book = {
      author: {
        name: 'Name 1',
        lastname: 'Lastname 1',
      },
      description: 'Description 2',
      pages: 24,
      progress: 0,
      title: `Title ${newId}`,
      uid: newId,
      screen: '',
    };
  }

  remove(id: number): void {
    const deleteIndex = this.books.findIndex((book) => book.uid === id);

    this.books.splice(deleteIndex, 1);
  }
}
